        source:  "https://rozetka.com.ua/search/",
        catalog: "https://rozetka.com.ua/search/"
    });

    RzSearchSuggest.setOptions({
        tpl_construct: {
            'suggest_history': '${main_begin}${word_additions_history}${main_end}',
        },
        part_tpls: {
            'word_additions_history':
                '{var i=0}' +
                '{for word_addition in records.words_additions}{var i=i+1}' +
                    '<div data-rz-gtm-event="suggestedQuery" data-name="${i-1}"" class="rz-header-search-suggest-i js_word_addition" name="${i-1}">' +
                    '<a class="rz-header-search-suggest-i-rezult" href="${word_addition.href}">${word_addition.name}</a>' +
                    '<a class="rz-suggest-delete js-rz-suggest-delete" data-rz-gtm-event="removeSuggestHistoryItem">' +
                        '<img src="https://i.rozetka.ua/design/_.gif" width="19" height="20" class="sprite-side rz-suggest-delete-icon">' +
                    '</a>' +
                    '</div>' +
                '{/for}' +
                '{if i>0}<div class="rz-header-search-suggest-i js-rz-search-suggest-clean-history" data-rz-gtm-event="cleanSuggestHistory">' +
                    '<a class="novisited" href="#">Очистить список</a>' +
                '</div>{/if}',
        },
    });
    RzSearchSuggest.init();
</script>

